using Microsoft.AspNetCore.Http;using Microsoft.AspNetCore.Mvc;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;using MyAppAPI.Helper;using MyAppAPI.Interface;using MyAppAPI.Model;namespace MyAppAPI.Controllers{    [Route("api/[controller]")]    [ApiController]    public class UsersController : ControllerBase    {        private IUserService _userService;        public UsersController(IUserService userService)        {            _userService = userService;        }        [HttpPost("authenticate")]        public IActionResult Authenticate(AuthenticateRequest model)        {            var response = _userService.Authenticate(model);            if (response == null)                return BadRequest(new { message = "Username or password is incorrect" });            return Ok(response);        }        [Authorize]        [HttpGet]        public IActionResult GetAll()        {            var users = _userService.GetAll();            return Ok(users);        }    }}